    public void processFinish(Scanner input) {
        //this you will received result fired from async class of onPostExecute(result) method.
        String line = input.nextLine();
        while (line != null) {
            if (line.contains("data:image")) {
                line = line.substring(line.indexOf(",") + 1);
                line = line.substring(0, line.indexOf("'"));
                break;
            }
            line = input.nextLine();
        }
//        input.close();
        TextView tv3 = (TextView) findViewById(R.id.textView4);
        tv3.setText("kk2 " + line.length());
    }

    public class MyCommentTask extends AsyncTask {
        @Override
        protected Object doInBackground(Object... params) {
            //descarrega metar
            try {
                URL url = new URL((String) params[0]);
                Scanner scanner = new Scanner(url.openStream());
//                scanner.nextLine();
                String line = scanner.nextLine();
                while (line != null) {
                    if (line.contains("comment-content")) {
                        line = line.substring(line.indexOf("comment-content") + 17);
                        line = line.substring(0, line.indexOf("</p>"));
                        return line;
                    }
                    line = scanner.nextLine();
                }
//		    	this.publishProgress(50);
            } catch (Exception e) {
                Log.e("error webcontenttask", e.toString());
            }
            return null;
        }
        protected void onPostExecute(Object result) {
//            TextView tv = (TextView) findViewById(R.id.textView4);
//            tv.setText((String) result);
            Toast.makeText(getApplicationContext(), (String) result, Toast.LENGTH_SHORT).show();
        }

    }
    public class MyCommentWebTask extends AsyncTask {
        @Override
        protected Object doInBackground(Object... params) {
        //descarrega metar
            try {
                URL url = new URL((String) params[0]);
                Scanner scanner = new Scanner(url.openStream());
//                scanner.nextLine();
                String line = scanner.nextLine();
                while (line != null) {
                    if (line.contains("JEROGL")) {
                        line = line.substring(line.indexOf("'") + 1);
                        line = line.substring(0, line.indexOf("'"));
                        return line;
                    }
                    line = scanner.nextLine();
                }
//		    	this.publishProgress(50);
            } catch (Exception e) {
                Log.e("error webcontenttask", e.toString());
            }
            return null;
        }

        protected void onPostExecute(Object result) {
            String str = (String) result;
            MyCommentTask comments = new MyCommentTask();
            comments.execute(str);
        }
    }
